function findElement(arr, func) {

  for (let i = 0; i < arr.length; i++) {

    if (func(arr[i])) {

      return arr[i];

    }

  }
  return undefined;
}

findElement([1, 3, 5, 8, 9, 10], function(num) { return num % 2 === 0; })


