function myReplace(str, before, after) {
let regex = /[A-Z]/;
let strSplit = str.split(" ");
let result = [...strSplit];
let beforePos = strSplit.indexOf(before);
let capAfter = after.charAt(0).toUpperCase()+ after.slice(1);



if(before.match(regex)) {
result.splice(beforePos, 1, capAfter);

}else if (before.match(regex)== null) {
result.splice(beforePos, 1, after);


}


return result.join(' ');
}

myReplace("He is Sleeping on the couch", "Sleeping", "sitting");
