function convertToRoman(num) {
let obj = {
0: null,1:"I",2:"II",3:"III",4:"IV",5:"V",6:"VI",7:"VII",8:"VIII",9:"IX",10:"X",20:"XX",30:"XXX",40:"XL",50:"L",60:"LX",70:"LXX",80:"LXXX",90:"XC",100:"C",200:"CC",300:"CCC",400:"CD",500:"D",600:"DC",700:"DCC",800:"DCCC",900:"CM", 1000:"M", 2000:"MM", 3000:"MMM"
};
let newA = String(num).split('').reverse();

let s = newA[0]; 
let t = newA[1] + 0;
let h = newA[2] + 0 + 0;
let th = newA[3] + 0 + 0 + 0;
let combo = [obj[th], obj[h], obj[t], obj[s]]; 
let clean = combo.filter(x => x !== undefined && x!==null);

return clean.join('');
}

