function frankenSplice(arr1, arr2, n) {
    arr1.slice(0, arr1.length);
    let newArr2 =  arr2.slice(0, arr2.length);
    newArr2.splice(n, 0, ...arr1);
    return newArr2;
}

frankenSplice(["claw", "tentacle"], ["head", "shoulders", "knees", "toes"], 2);


