function repeatStringNumTimes(str, num) {
    let emptyString = '';
    let repeatMe = [];
    if (num > 0) {
        for (let i = 0; i < num; i++) {
            repeatMe += str;
        }
        return repeatMe;
    } else {
        return emptyString;
    }
}

repeatStringNumTimes("abc", 3);
