function sumAll(arr) {
let newArr = [];
//compare 2 strings within arrays and return the larger number
const bigNum = Math.max.apply(null, arr);
const smallNum = Math.min.apply(null, arr);
//use the smaller number as starting point for the count

for (let i = smallNum; i <= bigNum; i++){

//loop the smaller number till it reaches the larger number, push to array
  newArr.push(i);

}

//add the sum of all the numbers within the array

let sum = newArr.reduce((a, b) => (a + b));

return sum;
}

sumAll([1, 4]);





